// Componente de contacto modernizado
// Componente de contacto modernizado
function Contact() {
  const [formData, setFormData] = useState({
    from_name: '',
    from_email: '',
    message: ''
  });
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setFormData({
    ...formData,
    [e.target.name]: e.target.value
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setStatus('Enviando...');

    // Usar el mismo correo para reply_to
    const dataToSend = {
      ...formData,
      reply_to: formData.from_email
    };

    try {
      const result = await emailjs.send(
        process.env.REACT_APP_EMAILJS_SERVICE_ID,
        process.env.REACT_APP_EMAILJS_TEMPLATE_ID,
        dataToSend,
        process.env.REACT_APP_EMAILJS_PUBLIC_KEY
      );

      console.log('Resultado:', result);
      setStatus('¬°Mensaje enviado correctamente!');
      setFormData({ from_name: '', from_email: '', message: '' });
    } catch (error) {
      console.error('Error al enviar el mensaje:', error);
      setStatus('Error al enviar el mensaje. Por favor, int√©ntalo de nuevo.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section id="contacto" className="section contact full-height">
      <div className="container">
        <div className="contact-container">
          <h2 className="section-title">Contacto</h2>

          <div className="contact-content">
            <div className="contact-info">
              <div className="contact-card">
                <h3 className="contact-subtitle">üí¨ ¬°Conversemos sobre tu proyecto!</h3>
                <p className="contact-text">
                  ¬øBuscas optimizar tu infraestructura? ¬øNecesitas implementar CI/CD?
                  ¬øQuieres mejorar la estabilidad de tus sistemas?
                </p>
                <p className="contact-text">
                  Con mi experiencia en <span className="highlight">DevOps, Kubernetes, Docker y Cloud</span>,
                  puedo ayudarte a transformar tus procesos de desarrollo y llevar tu
                  infraestructura al siguiente nivel.
                </p>

                <div className="contact-methods">
                  <div className="contact-method">
                    <div className="contact-icon">üìß</div>
                    <div>
                      <div className="contact-label">Email</div>
                      <div className="contact-value">contacto@example.com</div>
                    </div>
                  </div>
                  <div className="contact-method">
                    <div className="contact-icon">üì±</div>
                    <div>
                      <div className="contact-label">Tel√©fono</div>
                      <div className="contact-value">+123 456 7890</div>
                    </div>
                  </div>
                  <div className="contact-method">
                    <div className="contact-icon">üíº</div>
                    <div>
                      <div className="contact-label">LinkedIn</div>
                      <div className="contact-value">linkedin.com/in/rodrigo</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="contact-form-container">
              <div className="form-card">
                <h3 className="form-title">Env√≠ame un mensaje</h3>
                <form onSubmit={handleSubmit} className="contact-form">
                  <InputField
                    label="Nombre"
                    name="from_name"
                    value={formData.from_name}
                    handleChange={handleChange}
                  />
                  <InputField
                    label="Correo electr√≥nico"
                    name="from_email"
                    value={formData.from_email}
                    handleChange={handleChange}
                  />
                  <TextAreaField
                    label="Mensaje"
                    name="message"
                    value={formData.message}
                    handleChange={handleChange}
                  />
                  <button
                    type="submit"
                    className={`btn submit-btn ${loading ? 'loading' : ''}`}
                    disabled={loading}
                  >
                    {loading ? 'Enviando...' : 'Enviar mensaje'}
                  </button>

                  {status && (
                    <div className={`form-status ${status.includes('Error') ? 'error' : 'success'}`}>
                      {status}
                    </div>
                  )}
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

// Componente reutilizable para campos de entrada
function InputField({ label, name, value, handleChange }) {
  return (
    <div className="form-group">
      <label htmlFor={name}>{label}:</label>
      <input
        type={name === 'from_email' || name === 'reply_to' ? 'email' : 'text'}
        id={name}
        name={name}
        value={value}
        onChange={handleChange}
        placeholder={`Ingresa tu ${label.toLowerCase()}`}
        required
      />
    </div>
  );
}

// Componente reutilizable para textarea
function TextAreaField({ label, name, value, handleChange }) {
  return (
    <div className="form-group">
      <label htmlFor={name}>{label}:</label>
      <textarea
        id={name}
        name={name}
        value={value}
        onChange={handleChange}
        placeholder={`Escribe tu ${label.toLowerCase()}`}
        rows="5"
        required
      />
    </div>
  );
}

